ns9:component-version-script.script:
# ============================================================================================== # COMMENT: This sript is used to update windows apache server. It can only update below properties. # http_port # http_proxy_port # http_node_port # http_node_ips # appsrv_routes #Afer update is done , script stop the apache service and start it again. # ==============================================================================================
Write-Output "Start Apache server UPDATE on http_port, http_proxy_port, and/or http_node_port."
# This function check the error count. If any error it print the message and exit. function CheckError ($message){ if ($error.count -gt 0) { Write-Output $message exit $lastexitcode } }
#Replace existing virtual host configuration with give port function ReplaceVirtualHostContent(){ $source = Get-Content $config_httpd_conf | Out-String $pattern = "(?s)<VirtualHost \*:.*?>.*?</VirtualHost>" $newfile = [Regex]::Replace( $source, $pattern, "" ) Set-Content $config_httpd_conf $newfile; }
#Replace existing listen configuration with give port function ReplaceListenConfig(){ (Get-Content $config_httpd_conf) -replace '^Listen(.+)','' | Set-Content $config_httpd_conf }
$error.clear() $global:LASTEXITCODE = $null
#Tests if config file exist $fileexist = Test-Path $config_httpd_conf if ($fileexist -eq $false) { Write-Output "Abort. $config_httpd_conf does not exist!" exit $LASTEXITCODE }
$unsupported_props = @() foreach ($item in $APPD_UPDATE_PROPS) { if(!(Write-Output $item | Select-String -pattern "^(http_port|http_proxy_port|http_node_port|http_node_ips|appsrv_routes)")) { $unsupported_props = $unsupported_props + $item } } if ($unsupported_props) { Write-Output "This update script does not support the update of the following properties: $unsupported_props" exit 1 }
# Backup and update httpd config file $currentDate = (get-date).tostring("yyyy_M_dd-hh_mm_ss") $backUpFile= $config_httpd_conf + "_$currentDate" + ".bak" Write-Output "Taking back up of $config_httpd_conf to $backUpFile" copy $config_httpd_conf $backUpFile
Write-Output "Replacing all exiting Listen configuration" ReplaceListenConfig Write-Output "Replacing all exiting virtual host configuration" ReplaceVirtualHostContent
$new_conf_content = "`n`n"
# Enable http port if ($http_port) { Write-Output "Creating Listen config for http port: $http_port" $new_conf_content = $new_conf_content + "Listen " + $http_port + "`n"
Write-Output "Including updated virtual host config for http port" $new_conf_content = $new_conf_content + "`n" + "<VirtualHost *:" + $http_port + ">`n" + ` " DocumentRoot `"" + $config_static_site + "`"`n" + ` " ServerName localhost`n" + ` "</VirtualHost>`n" }
# Enable http proxy port if ($http_proxy_port) { Write-Output "Creating Listen config for http proxy port: $http_proxy_port" $new_conf_content = $new_conf_content + "Listen " + $http_proxy_port + "`n" }
#if there are node ips go inside and create virtual host config for same if (! $http_node_ips[0].Equals("None") -and $http_proxy_port) { Write-Output "Including virtual host config for http proxy port and http nodes"
$SUBSTR = ""
if ($use_ajp.Equals("YES")) {
for ($i=0; $i -lt $http_node_ips.Length; $i++) { $SUBSTR = $SUBSTR + "BalancerMember ajp://" + ` $http_node_ips[$i] + ":" + $http_node_port + "route=" + $appsrv_routes[$i] + "`n" }
$new_conf_content = $new_conf_content + "`n" + "<VirtualHost *:" + $http_proxy_port + ">`n" + ` " <Proxy balancer://mycluster>`n" + ` $SUBSTR + ` " </Proxy>`n" + ` " ProxyPass /" + $ajp_proxy_context +" balancer://mycluster/" + $tomcat_context+ "`n" + ` "</VirtualHost>`n"
} else {
for ($i=0; $i -lt $http_node_ips.Length; $i++) { $SUBSTR = $SUBSTR + "BalancerMember http://" + ` $http_node_ips[$i] + ":" + $http_node_port + " route=" + $appsrv_routes[$i] + "`n" }
$COOKIE_HEADER_DIRECTIVE = "" $STICKY_SESSION_NAME="JSESSIONID`|jsessionid"
if ($autogen_sticky_cookie.Equals("YES")) { $STICKY_SESSION_NAME = "ROUTEID" $COOKIE_HEADER_DIRECTIVE = "Header add Set-Cookie `"ROUTEID=.%{BALANCER_WORKER_ROUTE}e; path=/`" env=BALANCER_ROUTE_CHANGED" }
$new_conf_content = $new_conf_content + "`n" + "<VirtualHost *:" + $http_proxy_port + ">`n" + ` " <Proxy balancer://mycluster>`n" + ` $SUBSTR + ` " </Proxy>`n" + ` " ProxyPass / balancer://mycluster/ stickysession=" + $STICKY_SESSION_NAME + " nofailover=On`n" + ` " ProxyPassReverse / balancer://mycluster/`n" + ` " ProxyPreserveHost On`n" + ` $COOKIE_HEADER_DIRECTIVE + "`n" + ` "</VirtualHost>`n"
} }
$new_conf_content | Add-Content $config_httpd_conf
# Disable firewall on the desired ports netsh.exe firewall set portopening TCP $http_port "Apache Web Server" CheckError ("Error in disabling firewall on port:"+$http_port) netsh.exe firewall set portopening TCP $http_proxy_port "Apache Web Server" CheckError ("Error in disabling firewall on port:"+$http_proxy_port)
# Stop Server net stop Apache2.2 CheckError ("Error in stopping server") # Start Server net start Apache2.2 CheckError ("Error in starting server")
Write-Output "Finished Apache server UPDATE"

supported-os-details.exec-script:
$error.clear()
