ns9:component-version-script.script:
#!/bin/bash
# Import global conf
. $global_conf

set -e

#tmp staging dir for setting up installer
export INSTALL_PATH="/tmp/vmware/HQ"

LOG_FILE=/tmp/appdirector-agent.log

echo "Start to execute HQ Agent installation." >> ${LOG_FILE}
mkdir -p $INSTALL_PATH/packages

HQ_PACKAGE=
machine=$(uname -m)
case $machine in
    x86_64)
        HQ_PACKAGE=$HQ_PACKAGE_64
        ;;
    i*86)
        HQ_PACKAGE=$HQ_PACKAGE_32
        ;;
esac

mv $HQ_PACKAGE $INSTALL_PATH/packages/hq_agent_src.tar.gz

HQ_AGENT_INSTALL_DIR=/home/hyperic/
mkdir -p ${HQ_AGENT_INSTALL_DIR}
chmod 777 -R ${HQ_AGENT_INSTALL_DIR}
chmod 777 -R ${INSTALL_PATH}

echo "Unpack Hyperic Agent package." >> ${LOG_FILE}
tar -xvzf $INSTALL_PATH/packages/hq_agent_src.tar.gz -C $HQ_AGENT_INSTALL_DIR

# Hyperic agent home, e.g "/home/hyperic/hyperic-hq-agent-5.0"
HQ_AGENT_HOME=`find ${HQ_AGENT_INSTALL_DIR} -name "hyperic-hqee-agent*"`

echo $HQ_AGENT_HOME

HQ_AGENT_PROPS=${HQ_AGENT_HOME}/conf/agent.properties

if [ "x${HQ_AGENT_PORT}" != "x" ] ; then

    HQ_AGENT_PORT=${HQ_AGENT_PORT}
else

    HQ_AGENT_PORT=2144
fi

if [ "x${HQ_SERVER_IP}" != "x" ] ; then

    HQ_SERVER_IP=${HQ_SERVER_IP}
fi

echo "Start to write Hyperic Agent setup script to its property file." >> ${LOG_FILE}

echo "agent.listenPort=${HQ_AGENT_PORT}" >> ${HQ_AGENT_PROPS}
echo "agent.setup.camIP=${HQ_SERVER_IP}" >> ${HQ_AGENT_PROPS}
echo "agent.setup.camSecure=no" >> ${HQ_AGENT_PROPS}
echo "agent.setup.camPort=${HQ_SERVER_PORT}" >> ${HQ_AGENT_PROPS}
echo "agent.setup.camLogin=${HQ_ADMIN_USER}" >> ${HQ_AGENT_PROPS}
echo "agent.setup.camPword=${HQ_ADMIN_PASS}" >> ${HQ_AGENT_PROPS}
echo "agent.setup.agentIP=${ip_address}" >> ${HQ_AGENT_PROPS}
echo "agent.setup.agentPort=${HQ_AGENT_PORT}" >> ${HQ_AGENT_PROPS}
echo "agent.setup.resetupToken=no" >> ${HQ_AGENT_PROPS}
echo "agent.setup.newTransport=false" >> ${HQ_AGENT_PROPS}
echo "agent.setup.unidirectional=No" >> ${HQ_AGENT_PROPS}
echo "agent.setup.acceptUnverifiedCertificate=Yes" >> ${HQ_AGENT_PROPS}
echo "agent.logLevel=INFO" >> ${HQ_AGENT_PROPS}
echo "accept.unverified.certificates=true" >> ${HQ_AGENT_PROPS}
echo "hq.plugins.responsetime.disable=true" >> ${HQ_AGENT_PROPS}

echo "Finished Hyperic Agent installation." >> ${LOG_FILE}

