export PATH=$PATH:/usr/local/sbin:/usr/local/bin:/usr/sbin:/usr/bin:/sbin:/bin set -e
. $jboss_configure_script 2>&1 | tee /tmp/jboss_cfg_output

#!/bin/bash # Import global conf . $global_conf
export PATH=$PATH:/usr/local/sbin:/usr/local/bin:/usr/sbin:/usr/bin:/sbin:/bin set -e
service jboss start

# create the installion directory mkdir $JBOSS_HOME_DIR
$error.clear() . $UNZIP_EXE -q $JBOSS_PACKAGE_URL -d $JBOSS_HOME_DIR if ($error.count -gt 0) { exit $lastexitcode }
#$JBOSS_HOME="$JBOSS_HOME_DIR\$env:JBOSS_NAME_AND_VERSION\jboss-as" $JBOSS_HOME="$JBOSS_HOME_DIR\$JBOSS_NAME_AND_VERSION"
# Windows service extra package $error.clear() . $UNZIP_EXE -q -o $JBOSS_WINSVC_PACKAGE_URL -d $JBOSS_HOME if ($error.count -gt 0) { exit $lastexitcode }
