ns9:component-version-script.script:
# ============================================================================================== # COMMENT: This sript is used to update jboss server properties. It can only update below properties. # JBOSS_JMX_USER # JBOSS_JMX_PWD #Afer update is done , script stop the jboss service and start it again. # ==============================================================================================
function CheckError ($message){ if ($error.count -gt 0) { echo $message exit $lastexitcode } }
$error.clear() $global:LASTEXITCODE = $null echo "Start Jboss server UPDATE on JBOSS_JMX_USER,JBOSS_JMX_PWD"
$JBOSS_HOME="$JBOSS_HOME_DIR\$JBOSS_NAME_AND_VERSION" $JBOSS_INSTANCE_DIR="$JBOSS_HOME\server\$JBOSS_INSTANCE_NAME"
set-location $JBOSS_INSTANCE_DIR\conf\props
$JMX_USER_PROPERTIES_FILE="jmx-console-users.properties" $fileexist = Test-Path $JMX_USER_PROPERTIES_FILE
if ($fileexist -eq $false) { Write-Output "Abort. $JMX_USER_PROPERTIES_FILE does not exist!" exit $LASTEXITCODE }
$origFileName= $JMX_USER_PROPERTIES_FILE + ".update.properties" copy $JMX_USER_PROPERTIES_FILE $origFileName
$unsupported_props = @() foreach ($item in $APPD_UPDATE_PROPS) { if(!(echo $item | Select-String -pattern "^(JBOSS_JMX_USER|JBOSS_JMX_PWD)")) { $unsupported_props = $unsupported_props + $item } } if ($unsupported_props) { echo This update script does not support the update of the following properties: $unsupported_props exit 1 }
if ($APPD_PREV_JBOSS_JMX_USER) { echo "Replacing user" (Get-Content $JMX_USER_PROPERTIES_FILE | Out-String) -replace "$APPD_PREV_JBOSS_JMX_USER=","$JBOSS_JMX_USER=" | Set-Content $JMX_USER_PROPERTIES_FILE }
if ($APPD_PREV_JBOSS_JMX_PWD) { echo "Replacing password" (Get-Content $JMX_USER_PROPERTIES_FILE | Out-String) -replace "=$APPD_PREV_JBOSS_JMX_PWD","=$JBOSS_JMX_PWD" | Set-Content $JMX_USER_PROPERTIES_FILE }
# The Service Name is obtained from inside the service.bat script $serviceName="JBAS50SVC" # Stop Service Stop-Service $serviceName CheckError ("Error in stopping Service")
# Start Service Start-Service $serviceName CheckError ("Error in starting Service")
# Check and report status $serviceAfter=Get-Service $serviceName $serviceAfterStatus=$serviceAfter.status write-output "$serviceName is now $serviceAfterStatus"

