 if(!(echo $item | Select-String -pattern  "^(SA_PWD)")) {
	 $unsupported_props = $unsupported_props + $item
 }
}
if ($unsupported_props) {
	echo This update script does not support the update of the following properties:  $unsupported_props
	exit 1
}
