#!/bin/bash # Import global conf . $global_conf
export PATH=/usr/local/sbin:/usr/local/bin:/usr/sbin:/usr/bin:/sbin:/bin:/usr/games:/opt/vmware/bin:/opt/vmware/bin export HOME=/root #!/bin/sh
COMMANDVAR=""
PROGNAME=`basename $0`
##Funtion to Check Error function check_error() { if [ ! "$?" = "0" ]; then error_exit "$1"; fi }
## Function To Display Error and Exit function error_exit() { echo "${PROGNAME}: ${1:-"Unknown Error"}" 1>&2 exit 1 }
##Function to validate boolean values function check_boolean_value() { if [ ! "x$1" = "x" ]; then if [[ ! $1 == "true" && ! $1 == "false" ]]; then error_exit "Invalid value for parameter $2." else if [[ $1 == "true" ]]; then COMMANDVAR="${COMMANDVAR} $2"; fi fi fi }
##Function to validate the integer function valid_int() { local data=$1 if [[ $data =~ ^[0-9]{1,9}$ ]]; then return 0; else return 1 fi }
##Function to validate Ip address function valid_ip() { local ip=$1 local stat=1 if [[ $ip =~ ^[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}\.[0-9]{1,3}$ ]]; then OIFS=$IFS IFS='.' ip=($ip) IFS=$OIFS [[ ${ip[0]} -le 255 && ${ip[1]} -le 255 \ && ${ip[2]} -le 255 && ${ip[3]} -le 255 ]] stat=$? fi return $stat }
## Parameter Validation if [ ! "x${BINDIP}" = "x" ]; then if ! valid_ip $BINDIP; then error_exit "$pgm: Invalid Ip Address : $BINDIP." else COMMANDVAR="${COMMANDVAR} --bind_ip $BINDIP"; fi fi
if [ ! "x${DBPATH}" = "x" ]; then if [ ! -d $DBPATH ]; then mkdir -p $DBPATH; check_error "Directory Not Found for DBPATH : $DBPATH." fi COMMANDVAR="${COMMANDVAR} --dbpath $DBPATH"; fi
if [ ! "x${NSSIZE}" = "x" ]; then if ! valid_int $NSSIZE; then error_exit "Invalid value for parameter NSSSIZE." else COMMANDVAR="${COMMANDVAR} --nssize ${NSSIZE}"; fi fi
if [ ! "x${QUOTAFILES}" = "x" ]; then if ! valid_int $QUOTAFILES; then error_exit "Invalid value for parameter QUOTAFILES." else COMMANDVAR="${COMMANDVAR} --quotaFiles ${QUOTAFILES}"; fi fi
if [ ! "x${PORT}" = "x" ]; then if ! valid_int $PORT; then error_exit "Invalid value for parameter PORT." else COMMANDVAR="${COMMANDVAR} --port ${PORT}"; fi fi
shopt -s nocasematch
check_boolean_value $LOGAPPEND "--logappend" check_boolean_value $REST "--rest" check_boolean_value $CPU "--cpu" check_boolean_value $VERBOSE "--verbose" check_boolean_value $NOAUTH "--noauth" check_boolean_value $AUTH "--auth" check_boolean_value $NOPREALLOC "--nopreaddloc" check_boolean_value $QUOTA "--quota" check_boolean_value $NOTABLESCAN "--notablescan" check_boolean_value $NOSCRIPTING "--noscripting" check_boolean_value $NOHTTPINTERFACE "--nohttpinterface"
shopt -u nocasematch
## Creating Directory /opt/vmware/mongodb mkdir -p /opt/vmware/mongodb cd /opt/vmware/mongodb
## Cleanup rm -rf * check_error "Errors during cleanup.";
## Download MongoDB tar ball wget --output-document=mongodb.tgz $DOWNLOADURL check_error "Unable to download mongodb installer.";
## Extracting tar ball tar -xvf mongodb.tgz check_error "Unable to untar mongodb tar ball.";
## Saving MongoDB configuration if [ -f /etc/rc.d/rc.local ]; then BOOTRC=/etc/rc.d/rc.local elif [ -f /etc/rc.local ]; then BOOTRC=/etc/rc.local else error_exit "Unable to find init script to start service from" fi
if grep -Fxq "/opt/vmware/mongodb/*/bin/mongod $COMMANDVAR" $BOOTRC; then echo "Mongo DB Service already installed" else echo "/opt/vmware/mongodb/*/bin/mongod $COMMANDVAR" >> $BOOTRC check_error "Errors while saving configuration parmeters" fi
## MongoDB StartUp cd /opt/vmware/mongodb/*/bin
exec > /tmp/mongodbstartup 2>&1 setsid ./mongod $COMMANDVAR &

#!/bin/sh # Import global conf . $global_conf
export PATH=$PATH:/usr/local/sbin:/usr/local/bin:/usr/sbin:/usr/bin:/sbin:/bin
yum --nogpgcheck --noplugins -y install ntp

#!/bin/bash
# Import global conf . $global_conf
export PATH=/usr/local/sbin:/usr/local/bin:/usr/sbin:/usr/bin:/sbin:/bin:/usr/games:/opt/vmware/bin:/opt/vmware/bin export JAVA_HOME=/usr/java/default export PATH=$JAVA_HOME/bin:$PATH
if [ -x /usr/sbin/selinuxenabled ] && /usr/sbin/selinuxenabled; then if [ -x /usr/sbin/setenforce ]; then /usr/sbin/setenforce Permissive else echo 'SELinux is enabled.This may cause installation to fail.' fi fi
#########SCRIPT INTERNAL PARAMETERS######################## BEA_HOME="$webLogic_home" WLS_INSTALL_DIR="$BEA_HOME/WebLogic" WLSINSTALLERLOCATION="$WLS_INSTALL_DIR/INSTALLER" WLSINSTALLSCRIPT="$WLSINSTALLERLOCATION/wls_install.sh" SILENT_XML="$WLSINSTALLERLOCATION/silent.xml" NFS_PATH="$nfs_path" # This is the relative path to the current task location MOUNTPOINTLOCATION="tmp/mount" GROUP_NAME="$group_name" USER_NAME="$user_name"
## Function To Display Error and Exit function check_error() { if [ ! "$?" = "0" ]; then error_exit "$1"; fi }
function error_exit() { echo "${PROGNAME}: ${1:-"Unknown Error"}" 1>&2 exit 1 }
####################SCRIPT EXECUTION ################## echo "Paramter Validation" if [ "x${BEA_HOME}" = "x" ]; then check_error "WebLogic Home not set." fi if [ "x${GROUP_NAME}" = "x" ]; then check_error "Group Name not set." fi if [ "x${USER_NAME}" = "x" ]; then check_error "WebLogic user name not set." fi if [ "x${NFS_PATH}" = "x" ]; then check_error "NFS_PATH not set." fi echo "Paramter Validation - DONE"
#### ADDING THE DEDICATED GROUP AND WEBLOGIC USER echo "Adding WebLogic User" groupadd $GROUP_NAME useradd -g $GROUP_NAME -d $BEA_HOME $USER_NAME check_error "Errors during setting up WebLogic user accounts."; echo "Adding WebLogic User - DONE"
#Basic Directory Structure mkdir -p $WLSINSTALLERLOCATION mkdir -p $MOUNTPOINTLOCATION check_error "Errors during creating basic direcotry structure.";
echo "Mounting WebLogic Installer..." if [ -f /etc/redhat-release ] ; then DIST=`cat /etc/redhat-release |sed s/\ release.*//` if [ "$DIST" = "CentOS" ] ; then yum -y install nfs-utils /sbin/service portmap start check_error "Error: Portmap Not started" else yum update -y yum -y install nfs-utils ld-linux.so.2 /sbin/service rpcbind start check_error "Error: Portmap Not started" fi elif [ -f /etc/debian_version ] ; then DistroBasedOn='Debian' apt-get update -y apt-get -f -y install apt-get -y install nfs-common service portmap restart check_error "Error: Portmap Not started" else /sbin/service rpcbind start check_error "Error: Portmap Not started" fi echo "rpcbind call done... "
mount $NFS_PATH $MOUNTPOINTLOCATION check_error "Errors during mounting WebLogic installer.";
# Copy WebLogic 12c Installer cp $MOUNTPOINTLOCATION/webLogicInstaller.bin $WLSINSTALLERLOCATION check_error "Errors during copying WebLogic installer"; echo "Mounting WebLogic 12c Installer - DONE"
chown -R $USER_NAME:$GROUP_NAME $INSTALL_BASE chown -R $USER_NAME:$GROUP_NAME $BEA_HOME chown -R $USER_NAME:$GROUP_NAME $WLS_INSTALL_DIR chown -R $USER_NAME:$GROUP_NAME $WLSINSTALLERLOCATION chmod -R 775 $INSTALL_BASE chmod -R 775 $BEA_HOME chmod -R 775 $WLS_INSTALL_DIR chmod -R 775 $WLSINSTALLERLOCATION
# CREATING SILENT XML FILE echo "Creating Silent.xml installation file for WebLogic" cat <<EOF> $SILENT_XML <?xml version="1.0" encoding="UTF-8"?>
<bea-installer> <input-fields> <data-value name="BEAHOME" value="$BEA_HOME" /> <data-value name="WLS_INSTALL_DIR" value="$WLS_INSTALL_DIR"/> <data-value name="COMPONENT_PATHS" value="WebLogic Server|Oracle Enterprise Pack for Eclipse|Oracle Coherence/Coherence Product Files|Oracle Coherence/Coherence Examples"/> <data-value name="USE_EXTERNAL_ECLIPSE" value="false"/> <data-value name="EXTERNAL_ECLIPSE_DIR" value="$WLS_INSTALL_DIR/eclipse/eclipse32" /> </input-fields> </bea-installer> EOF check_error "Error while creating Silent.xml file" echo "Silent.xml file created Successfully" chmod -R 775 $SILENT_XML
cat << EOF > $WLSINSTALLSCRIPT #!/bin/bash cd $WLSINSTALLERLOCATION ./webLogicInstaller.bin -mode=silent -silent_xml=$SILENT_XML EOF check_error "Error while creating installation file"
chmod -R 775 $WLSINSTALLSCRIPT echo "Installing WebLogic Server" su - $USER_NAME -c $WLSINSTALLSCRIPT check_error "Errors during installing WebLogic installer.";
# clean up umount $MOUNTPOINTLOCATION check_error "Errors during umounting $MOUNTPOINTLOCATION.";
echo "Installed Successfully"

#!/bin/bash
# Import global conf . $global_conf
PATH=/usr/local/sbin:/usr/local/bin:/usr/sbin:/usr/bin:/sbin:/bin:/usr/games:/opt/vmware/bin:/opt/vmware/bin export JAVA_HOME=/usr/java/default export PATH=$JAVA_HOME/bin:$PATH
#########CUSTOMIZED PARAMETERS######### BEA_HOME="$webLogic_home" WLS_INSTALL_DIR="$BEA_HOME/WebLogic" WLSINSTALLERLOCATION="$WLS_INSTALL_DIR/INSTALLER" WLSCONFIGURATIONSCRIPT="$WLSINSTALLERLOCATION/wls_config.sh" SILENT_SCRIPT="$WLSINSTALLERLOCATION/silent_cw.sh" DOMAIN_NAME="$domain_name" SERVER_NAME="$server_name" ADMIN_USERNAME="$admin_user_name" ADMIN_PASSWORD="$admin_password" LISTENING_PORT="$listening_port" SERVER_SSLLISTEN_PORT="$server_ssl_listen_port" GROUP_NAME="$group_name" USER_NAME="$user_name"
###########Paramter Validation Functions################## ## Function To Display Error and Exit function check_error() { if [ ! "$?" = "0" ]; then error_exit "$1"; fi }
function error_exit() { echo "${PROGNAME}: ${1:-"Unknown Error"}" 1>&2 exit 1 }
## Function To Validate Integer function valid_int() { local data=$1 if [[ $data =~ ^[0-9]{1,9}$ ]]; then return 0; else return 1 fi }
####################SCRIPT EXECUTION ################## echo "Paramter Validation"
if [ "x${DOMAIN_NAME}" = "x" ]; then error_exit "WEBLOGIC_DOMAIN_NAME not set." fi if [ "x${SERVER_NAME}" = "x" ]; then error_exit "WEBLOGIC_SERVER_NAME not set." fi
if [ "x${ADMIN_USERNAME}" = "x" ]; then error_exit "ADMIN_USERNAME not set." fi
if [ "x${ADMIN_PASSWORD}" = "x" ]; then error_exit "ADMIN_PASSWORD not set." fi
if [ "x${LISTENING_PORT}" = "x" ]; then error_exit "LISTENING_PORT not set." else if ! valid_int $LISTENING_PORT; then error_exit "Invalid parameter LISTENING_PORT" fi fi
if [ "x${SERVER_SSLLISTEN_PORT}" = "x" ]; then error_exit "SERVER_SSLLISTEN_PORT not set." else if ! valid_int $SERVER_SSLLISTEN_PORT; then error_exit "Invalid parameter SERVER_SSLLISTEN_PORT" fi fi
echo "Paramter Validation - DONE"
# CREATING SILENT SCRIPT FILE echo "Creating silent configuration script for WebLogic Server" cat <<EOF> $SILENT_SCRIPT read template from "$WLS_INSTALL_DIR/common/templates/domains/wls.jar"; create server "$SERVER_NAME" as serv1; create User "$ADMIN_USERNAME" as us1; set us1.Password "$ADMIN_PASSWORD"; assign User "$ADMIN_USERNAME" to Group "Deployers";
find Server "$SERVER_NAME" as s1; set s1.ListenAddress ""; set s1.ListenPort "$LISTENING_PORT"; set s1.SSL.Enabled "true"; set s1.SSL.ListenPort "$SERVER_SSLLISTEN_PORT";
find User "weblogic" as u2; set u2.password "welcome1";
set OverwriteDomain "true";
write domain to "$WLS_INSTALL_DIR/user_projects/domains/$DOMAIN_NAME";
close template; EOF check_error "Error while creating silent script file" echo "Configuration file created successfully" chmod -R 775 $SILENT_SCRIPT
# CREATING WEBLOGIC CONFIGURATION SCRIPT FILE cat << EOF > $WLSCONFIGURATIONSCRIPT #!/bin/bash cd $WLS_INSTALL_DIR/common/bin ./config.sh -mode=silent -silent_script=$SILENT_SCRIPT EOF check_error "Error while creating Configuration file"
chmod -R 775 $WLSCONFIGURATIONSCRIPT echo "Configuring Domain for WebLogic Server..." su - $USER_NAME -c $WLSCONFIGURATIONSCRIPT check_error "Errors during configuring WebLogic server."; echo "Configured Successfully."

#!/bin/bash
#########CUSTOMIZED PARAMETERS######### INSTALL_BASE="$install_base" INSTALL_LOCATION="$INSTALL_BASE/WebSphere/AppServer"
#########PARAMTERS FROM APP DIRECTOR######### PROFILE_NAME="$profile_name" PROFILE_PATH="$INSTALL_BASE/WebSphere/AppServer/profiles/$PROFILE_NAME" NODE_NAME="$node_name" SERVER_NAME="$server_name" ADMIN_USERNAME="$admin_username" ADMIN_PASSWORD="$admin_password"
# FUNCTION TO DISPLAY ERROR AND EXIT function check_error() { if [ ! "$?" = "0" ]; then error_exit "$1"; fi } function error_exit() { echo "${PROGNAME}: ${1:-"Unknown Error"}" 1>&2 exit 1 }
echo $SHELL
#CHANGING THE SHELL FROM DASH TO BASH cd /bin unlink sh ln -s /bin/bash sh echo $SHELL
# CREATING PROFILE if [ "x${profile_path}" != "x" ]; then PROFILE_PATH="$profile_path" fi JAVA_HOME="/usr/lib/jvm/jre-1.6.0" WAS_HOME=$INSTALL_LOCATION
cd $INSTALL_LOCATION/bin echo "CREATING WEBSPHERE PROFILES" ./manageprofiles.sh -create -profileName $PROFILE_NAME -serverName $SERVER_NAME -profilePath $PROFILE_PATH -nodeName $NODE_NAME -portsFile $INSTALL_LOCATION/profileTemplates/default/actions/portsUpdate/portdef.props -profileTemplate $INSTALL_LOCATION/profileTemplates/default -adminUserName $ADMIN_USERNAME -adminPassword $ADMIN_PASSWORD check_error "ERRORS DURING CREATING PROFILE.";

#!/bin/bash

## FUNCTION TO DISPLAY ERROR AND EXIT
function check_error()
{
   if [ ! "$?" = "0" ]; then
      error_exit "$1";
   fi
}

echo "STARTING E-BUSINESS SERVER"
INSTALL_BASE="$install_base"
INSTALL_LOCATION="$INSTALL_BASE/WebSphere/AppServer"
SERVER_NAME="$server_name"

cd $INSTALL_LOCATION/bin
./startServer.sh $SERVER_NAME
check_error "ERRORS DURING STARTING SERVER.";

#!/bin/sh # vFabric ApplicationDirector Sample START script for vFabric GemFire
# This example uses the values posted below as defaults. To change any of these # values, add the Property Name as shown below as individual properties in your # service definition in the ApplicationDirector Catalog. The value specified after # the Property name is the Type to use for the property (i.e. String, Content, Array etc) # There are two types of properties for this script: Required and Optional. Both are # listed below. # # REQUIRED PROPERTIES: # These are the properties you must add in order for this sample script to work. The property # is added when you create your service definition in the ApplicationDirector Catalog. # Property Description: Property Value settable in blueprint [type]: # -------------------------------------------------------------------------------------------- # Location of global configuration data global_conf [Content] # value: https://${darwin.server.ip}:8443/darwin/conf/darwin_global.conf # # OPTIONAL PROPERTIES: # Property Description: Property Name settable in blueprint: # -------------------------------------------------------------------------------------------- # which java to use JAVA_HOME [String] # Installed Location of GemFire GEMFIRE_HOME [String] # GemFire version installed (default is 663) GEMFIRE_VERSION [String]
# From ApplicationDirector - Import and source global configuration # . $global_conf
# This sample script simply starts a CacheServer from using the cacheserver service call setup from using the RPM install.
set -e echo "######################################################################" echo "#" echo "# Starting vFabric Gemfire From Application Director" echo "#" echo "######################################################################"
export PATH=$PATH:/usr/sbin:/sbin:/usr/bin:/bin export VMWARE_HOME=/opt/vmware export GEMFIRE_PACKAGE=vfabric-gemfire export GEMFIRE_VERSION=${GEMFIRE_VERSION:="663"} export GEMFIRE_HOME=${GEMFIRE_HOME:="$VMWARE_HOME/$GEMFIRE_PACKAGE/vFabric_GemFire_$GEMFIRE_VERSION"}
# Any of the following may be set as Properties in your service definition, and if enabled, may be overwritten # in your application blueprint. export JAVA_HOME=${JAVA_HOME:="/usr"}
if [ -f ${GEMFIRE_HOME}/bin/gemfire ]; then service cacheserver start else echo "ERROR! GemFire executable not found in ${GEMFIRE_HOME}; Exiting" exit fi echo "######################################################################" echo "#" echo "# vFabric Gemfire Start Script Finished" echo "#" echo "######################################################################"
